import React from 'react';
import Footer from '../footer/footer';
import Header from '../header/header';

export default class MainTemplate extends React.Component{
    constructor(props){
        super(props)
        var showHeader;
        if(this.props.location == '/home'){
            showHeader = <Header/>
        }
        else{
            showHeader = ''
        }
    }
    render(){
        return(
            <div>
                {showHeader}
                {this.props.children}
                <Footer/>    
            </div>
        )
    }
}
