import React from 'react';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import Grid from '@material-ui/core/Grid';
import Container from '@material-ui/core/Container';
import IconButton from '@material-ui/core/IconButton';
import axios from 'axios';
const {BASE_URL} = require('../../config'); 
import Cookie from 'universal-cookie';

export default class History extends React.Component{
    constructor(props){
        super(props)
        console.log(props)
        this.state = {
            image:'',
        }
    }
    componentDidMount(){
        function _arrayBufferToBase64( buffer ) {
    var binary = '';
    var bytes = new Uint8Array( buffer );
    var len = bytes.byteLength;
    for (var i = 0; i < len; i++) {
        binary += String.fromCharCode( bytes[ i ] );
    }
    return window.btoa( binary );
}
        this.image = ""
        var cookie = new Cookie();
        var username = cookie.get('username');
        var pair = this.props.history.location.pathname.split('/')[2]
        var that = this;
        axios.get(BASE_URL+'/image', {params:{username:username, pair:pair}}).then(
            function(result){
                console.log(result)

                that.setState({image: 'data:image/png;base64,'+result.data});
            },
            function(error){

            }
        )
    }
    render(){
        return(
        <div style={{marginTop:'64px', width:'100%'}}>
            <Container width='xl' maxWidth='xl' style={{height:'600px', margin:'0px', padding:'0px'}}>
                <Grid container spacing={1} width='lg' style={{height:'inherit'}}>
                    <Grid item lg={1} style={{margin:'auto', textAlign:'center'}}>
                        <IconButton><ChevronLeftIcon/></IconButton>
                    </Grid>
                    <Grid item lg={10} style={{margin:'auto', textAlign:'center'}}>
                        <img src={this.state.image} alt=""/>
                    </Grid>
                    <Grid item lg={1} style={{margin:'auto', textAlign:'center'}}>
                        <IconButton>
                            <ChevronRightIcon/>  
                        </IconButton>                        
                    </Grid>
                </Grid>   
            </Container>             
        </div> 
        )
    }
}
