import React from 'react';
import DashRender from './dashboardRender';
import DashboardService from './dashboardService';
import Cookie from 'universal-cookie';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import TextField from '@material-ui/core/TextField';

export default class Dashboard extends React.Component{
    constructor(props){
        super(props);
        const cookie = new Cookie();
        const token = cookie.get('token');
        const username = cookie.get('username');
        const dashboardService = new DashboardService();
        this.data = []
        dashboardService.getDashboard(token, username,
            this.onSuccess.bind(this), 
            this.onError.bind(this)
        );
    }
    onSuccess(data){
        this.data = data
        this.setState({dataRetrived:true})
    }
    onError(error){
        console.log(error)
    }
    render(){
        var years = [ 
            {value:2020, label:'2020'},
            {value:2021, label:'2021'}
        ]
        return(
            <Grid container spacing={1}>
                <Grid item xs={12} md={12} lg={2} style={{marginTop:"96px", paddingLeft:"70px"}}>
                    <Paper>
                        <TextField
                        id="select_year"
                        select
                        value={years}
                        helperText="Anno"
                        style={{width:"100%", padding:"5px"}}
                        />
                        <TextField
                        id="select_month"
                        select
                        value={[ 
                            {value:2020, label:'2020'},
                            {value:2021, label:'2021'}
                        ]}
                        helperText="Mese"
                        style={{width:"100%", padding:"5px"}}
                        />
                    </Paper>
                </Grid>
                <Grid item xs={12} md={12} lg={10}>
                    <DashRender data={this.data}/>  
                </Grid>  
            </Grid>

        )
    }
}
