if(process.env.NODE_ENV == 'development'){
    const BASE_URL = 'http://localhost:3000/testAPI'
    const HOST_PORT = 'http://localhost:3000'    
}
else{
    const BASE_URL = 'https://expressapireact2.herokuapp.com:3000/testAPI'
    const HOST_PORT = 'https://expressapireact2.herokuapp.com:3000'
}


module.exports = {BASE_URL, HOST_PORT};

console.log(process.env.NODE_ENV)
