import React from 'react';
import DashRender from './dashboardRender';
import DashboardService from './dashboardService';
import Cookie from 'universal-cookie';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Autocomplete from '@material-ui/lab/Autocomplete';
import TextField from '@material-ui/core/TextField';

export default class Dashboard extends React.Component{
    constructor(props){
        super(props);
        const cookie = new Cookie();
        const token = cookie.get('token');
        const username = cookie.get('username');
        const dashboardService = new DashboardService();
        this.data = []
        dashboardService.getDashboard(token, username,
            this.onSuccess.bind(this), 
            this.onError.bind(this)
        );
    }
    onSuccess(data){
        this.data = data
        this.setState({dataRetrived:true})
    }
    onError(error){
        console.log(error)
    }
    render(){
        return(
            <Grid container spacing={1}>
                <Grid item xs={12} md={12} lg={1}>
                    <Autocomplete
                        id="selector_year"
                        options={{2020:'2020', 2021:'2021'}}
                        style={{width:300}}
                        renderInput={ params => {
                           <TextField {...params} label="Anno" variant="outlined" fullWidth/> 
                        }}
                    />

                </Grid>
                <Grid item xs={12} md={12} lg={11}>
                    <DashRender data={this.data}/>  
                </Grid>  
            </Grid>

        )
    }
}
