import React from 'react';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import Grid from '@material-ui/core/Grid';
import Container from '@material-ui/core/Container';
import IconButton from '@material-ui/core/IconButton';
import Paper from '@material-ui/core/Paper';
import axios from 'axios';
import Cookie from 'universal-cookie';
const { BASE_URL, HOST_PORT } = require('../../config');

export default class History extends React.Component {
    constructor(props) {
        super(props)
        console.log(props)
        this.state = {
            image: '',
            imagesData:[],
            imageIndex:0
        }
        var cookie = new Cookie();
        var username = cookie.get('username');
        this.pair = this.props.history.location.pathname.split('/')[2]
        this.month = 1;
        this.year = 2020;
    }
    previousImage(){
        if(this.state.imageIndex == 0){
            this.setState({imageIndex: this.imageData.length-1})
        }
        else{
            this.setState({imageIndex: this.state.imageIndex-1})
        }
    }
    nextImage(){
        if(this.state.imageIndex == this.state.imagesData.length)
        {
            this.setState({imageIndex: 0})
        }
        else{
            this.setState({imageIndex: this.state.imageIndex+1})    
        }
    }

    componentDidMount() {
        var that = this
        axios.get(BASE_URL + '/imagesData', { params: { 
                username: this.username, 
                pair: this.pair,
                month: this.month,
                year: this.year
            } }).then(
            function (result) {
                console.log(result)
                that.setState({imagesData: result.data})
            },
            function (error) {
                console.log(error)
            }
        )
    }
    render() {

        var image = HOST_PORT+'/'+username+'/'+pair+'/'+this.state.imagesData[this.state.imageIndex].filename;
        return (
            <div style={{ paddingTop: '70px', width: '100%' }}>
                <Container width='xl' maxWidth='xl' style={{ height: '600px', margin: '0px', padding: '0px', display: 'flex' }}>
                    <Grid container spacing={0} width='lg' style={{ height: 'inherit', }}>
                        <Grid item xs={12} sm={2} lg={2}></Grid>
                        <Grid item xs={12} sm={1} lg={1} style={{ margin: 'auto', textAlign: 'center' }}>
                            <IconButton
                                onClick={this.previousImage}
                            ><ChevronLeftIcon /></IconButton>
                        </Grid>
                        <Grid item xs={12} sm={8} lg={8} style={{ margin: 'auto', textAlign: 'center', display: 'flex' }}>
                            <Paper style={{padding:'5px'}}>
                                <img src={image} alt="" style={{ maxWidth: "100%", display: 'flex' }} />   
                            </Paper>
                        </Grid>                       
                        <Grid item xs={12} sm={1} lg={1} style={{ margin: 'auto', textAlign: 'center' }}>
                            <IconButton
                                onClick={this.nextImage}
                            ><ChevronRightIcon />
                            </IconButton>
                        </Grid>
                        <Grid item xs={12} sm={12} lg={12}></Grid>
                    </Grid>
                </Container>
            </div>
        )
    }
}
